/* USAGE
 * RESULT
|--------------------------------------------------------------------------
| h1 { font-size: fontsize('ms1); }
| h1 { font-size: 19.2px; }
|--------------------------------------------------------------------------
| h1 { @include fontsize('ms1', 1.2); }
| h1 { font-size: 19.2; line-height: 1.2; }
|--------------------------------------------------------------------------
*/

/* stylelint-disable */
@function fontsize($key) {
  @if map-has-key($app-fontsizes, $key) {
