.c-input,
.c-input:not([type='submit']) {
  appearance: none;
  display: inline-block;

  width: 100%;

  margin-bottom: verticalrhythm(1);
  padding: space(1);

  transition: all 0.5s ease('out-cubic');

  color: color('black-90');
  border: 1px solid c('black-10');
  outline: 0;
  background: darken(c('white'), 5%);

