c-radio {
  position: absolute;
  left: -9999px;

  + span {
    position: relative;

    margin: 0 30px 0 0;
    padding: 0 0 0 26px;

    cursor: pointer;

    &::before {
      position: absolute;
      top: 0;
      left: 0;


      width: 16px;
      height: 16px;
      content: '';
      border-radius: 40px;

      background: color('white');
    }
  }

  &:checked + span {

    &::before {
      background: color('black-15');
      box-shadow: inset 0 0 0 4px c('white');
    }

  }
}
